package Functions;

public class Basics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello! World");
		add();
		System.out.println("Bye");
	
	}
	
	public static void add() {
			int a=8;
			int b=7;
			int c=a+b;
			sub();
			System.out.println(c);
		}
	public static void sub() {
		int a=8;
		int b=7;
		int c=a-b;
		System.out.println(c);
	}

	
}
